# Define the grocery shop inventory using a dictionarygrocery_shop_inventory = {    'apples' : 10,    'bananas': 5,    'oranges': 15,    'carrots': 20,    'eggs'   : 30,}# Define a function to display the inventorydef display_inventory(inventory):    print("Grocery Shop Inventory:")    for item, quantity in inventory.items():        print(f"{item}: {quantity}")# Define a function to check if an item is in stockdef check_stock(item, inventory):    if item in inventory and inventory[item] > 0:        return True    else:        return False# Define a function to purchase an itemdef purchase(item, inventory):    if check_stock(item, inventory):        inventory[item] -= 1        print(f"Purchased: {item}")    else:        print(f"Sorry, {item} is out of stock")# Define a list of items to purchaseitems_to_purchase = ['apples', 'bananas', 'oranges', 'carrots', 'eggs']# Purchase each item in the listfor item in items_to_purchase:    purchase(item, grocery_shop_inventory)	# Display the inventory after the purchasesdisplay_inventory(grocery_shop_inventory)